package com.zhihu.matisse.sample;import android.app.Activity;import android.os.Bundle;import android.support.annotation.Nullable;import android.view.View;import android.widget.Button;import android.widget.TextView;import java.security.MessageDigest;import java.security.NoSuchAlgorithmException;/** * Created by boboz on 2018/4/2. */public class TestActivity extends Activity  implements View.OnClickListener{    String actionkey="action";    String actionValue="youku.api.mengkai.query";    String client_idkey="client_id";    String client_idvalue="1RS8Mkgue8ouGrYv";    String timestampkey="timestamp";    String timestampvalue="1523933329";    String version_key="version";    String version_value="3.0";    private TextView textView;    Button showButton;    @Override    protected void onCreate(@Nullable Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.test_demo);        textView=findViewById(R.id.text_showsign);        showButton=findViewById(R.id.btn_startsign);        showButton.setOnClickListener(this);    }    public static String crypt(String str) {        if (str == null || str.length() == 0) {            throw new IllegalArgumentException("String to encript cannot be null or zero length");        }        StringBuffer hexString = new StringBuffer();        try {            MessageDigest md = MessageDigest.getInstance("MD5");            md.update(str.getBytes());            byte[] hash = md.digest();            for (int i = 0; i < hash.length; i++) {                if ((0xff & hash[i]) < 0x10) {                    hexString.append("0" + Integer.toHexString((0xFF & hash[i])));                } else {                    hexString.append(Integer.toHexString(0xFF & hash[i]));                }            }        } catch (NoSuchAlgorithmException e) {            e.printStackTrace();        }        return hexString.toString();    }    @Override    public void onClick(View view) {        switch (view.getId()){            case R.id.btn_startsign:                String signword=actionkey+actionValue+client_idkey+client_idvalue+timestampkey+timestampvalue+version_key+version_value;                textView.setText(crypt(signword));                break;        }    }}